import React from 'react';
import { useHistory } from 'react-router-dom';
import '../styles/SelectedArticle.css'; // Assurez-vous de créer ce fichier CSS

function SelectedArticle({ article }) {
    const history = useHistory();

    const handleGoBack = () => {
        history.push('/');
    };

    return (
        <div className="selected-article-container">
            <div className="article-header">
                <div className="name"><b>{article.name}</b></div>
                <div className="author">
                    <p><b>Auteur:</b> {article.autheur}</p>
                    <img src={article.avatar} alt='avatar-auteur' className='avatar' />
                </div>
            </div>
            <div className="article-content">
                <p>{article.text}</p>
                <p className="date"><b>{article.date}</b></p>
            </div>
            <button onClick={handleGoBack}>Retour à la liste des articles</button>
        </div>
    );
}

export default SelectedArticle;
